    async def fetch_markets(self, params={}):
        allInstruments = []
        # Fetch available currencies
        currenciesResponse = await self.publicGetGetCurrencies()
        currencies = self.safe_value(currenciesResponse, 'result')
        # Fetch instruments per currency
        for i in range(0, len(currencies)):
            instrumentsResponse = await self.publicGetGetInstruments({'currency': currencies[i]['currency']})
            instruments = self.safe_value(instrumentsResponse, 'result')
            allInstruments = allInstruments.concat(instruments)
        # Result object formatting
        result = []
        for i in range(0, len(allInstruments)):
            market = allInstruments[i]
            id = self.safe_string(market, 'instrument_name')
            baseId = self.safe_string(market, 'base_currency')
            quoteId = self.safe_string(market, 'currency')
            base = self.safe_currency_code(baseId)
            quote = self.safe_currency_code(quoteId)
            result.append({
                'id': id,
                'symbol': id,
                'base': base,
                'quote': quote,
                'active': market['is_active'],
                'precision': {
                    'amount': market['min_trade_amount'],
                    'price': market['tick_size'],
                },
                'limits': {
                    'amount': {
                        'min': market['min_trade_amount'],
                    },
                    'price': {
                        'min': market['tick_size'],
                    },
                },
                'type': market['kind'],
                'spot': False,
                'future': market['kind'] == 'future',
                'option': market['kind'] == 'option',
                'info': market,
            })
        return result

