    def nonce(self):
        return self.milliseconds()

    def random_nonce(self, length):
        result = self.hash(self.encode(self.apiKey + str(self.nonce())), 'sha512', 'base64')
        return result[0:length]

    def sign(self, path, api='public', method='GET', params={}, headers=None, body=None):
        query = '/' + 'api/' + self.version + '/' + api + '/' + path
        url = self.urls['api'] + query
        # If public or private endpoint
        if api == 'public':
            if params:
                url += '?' + self.urlencode(params)
        else:
            self.check_required_credentials()
            uri = query
            if params:
                uri += '?' + self.urlencode(params)
                params = self.keysort(params)
            requestBody = ''
            if body:
                requestBody = body
            timestamp = (self.nonce())
            randomNonce = self.random_nonce(16)
            # Craft relevant data
            requestData = method.upper() + '\n' + uri + '\n' + requestBody + '\n'
            stringToSign = timestamp + '\n' + randomNonce + '\n' + requestData
            signature = self.hmac(self.encode(stringToSign), self.encode(self.secret), hashlib.sha256)
            # Craft header
            headerVal = 'deri-hmac-sha256 ' + 'id=' + self.apiKey + ',ts=' + timestamp + ',nonce=' + randomNonce + ',sig=' + self.encode(signature)
            headers = {
                'Authorization': headerVal,
            }
            if method != 'GET':
                headers['Content-Type'] = 'application/x-www-form-urlencoded'
                body = self.urlencode(params)
            elif params:
                url += '?' + self.urlencode(params)
        return {'url': url, 'method': method, 'body': body, 'headers': headers}

    def handle_errors(self, httpCode, reason, url, method, headers, body, response):
        if not response:
            return  # fallback to default error handler
        error = self.safe_string(response, 'error')
        if (error is not None) and(error != '0'):
            feedback = self.id + ' ' + body
            exceptions = self.exceptions
            if error in exceptions:
                raise exceptions[error](feedback)
            raise ExchangeError(feedback)  # unknown message
